import classes from './Intro.module.css';

const Intro = () => {

    return (
    <Fragment>
        <div className={classes.container}>
            <div className={classes.textContainer}>
                <p className={classes.TitletextStyle}>Hello World!</p> 
                <p className={classes.normalTextStyle}>My name is <span>Rita Saraiva</span> and I'm a AR/VR Developer, 
                Frontend Developer and a Multimedia Designer</p>
            </div>
            <img src={RoundCharacter} alt="Character whsiling" className={classes.imageSize}/> 
        </div>
        <img  src={arrowIcon} alt="arrow Icon" />
    </Fragment>
    );
  };
  
  export default Intro;
