import React, { useState } from 'react';
import classes from './TaskBar.module.css';
import Logo from '../../src/Assets/Logo.png';
import Line from './UI/Line/Line';

const TaskBar = (props) => {
  
    const [lineWidth, setLineWidth] = useState(0);
  
    const handleItemClick = (width) => {
      setLineWidth(width);
    };

    const itemList = [
      { label: 'Home', onClick: () => { props.onClickHome(); handleItemClick(80); } },
      { label: 'About', onClick: props.onClickAbout },
      { label: 'Experience', onClick: props.onClickExperience },
      { label: 'Projects', onClick: props.onClickProjects },
      { label: 'Contact', onClick: props.onClickContact },
    ];

  const openPdf = () => {
    const pdfUrl = process.env.PUBLIC_URL + '/CV_RitaSaraiva.pdf';

    window.open(pdfUrl, '_blank');
  };

  return (
    <div className={classes.IDK}>
      <div className={classes.container}>
        <div className={classes.coisa}>
          <img  src={Logo} alt="Logo" className={classes.ImagimeStyle}/>
          <div className={classes.taskOptionsContainer}>
            <ul className={classes.buttonsContainer}>
              {itemList.map((item, index) => (
                <li className={classes.buttonStyle} key={index} onClick={item.onClick}>
                  <div className={classes.lineContainer}>
                    {item.label}
                    <Line lineWidth={lineWidth} />
                  </div>
                </li>
              ))}
            </ul>
          </div>
          <button className={classes.resumeButtonContainer} onClick={openPdf}>RESUME</button>
        </div>
      </div>
    </div>
  );
};

export default TaskBar;
