import React, { useState } from 'react';
import classes from './TaskBar.module.css';
import Logo from '../../src/Assets/Logo.png';
import Line from './UI/Line/Line';

const TaskBar = (props) => {

    const [activeItem, setActiveItem] = useState(null);

  const handleItemClick = (label) => {
    setActiveItem(label);
  };

  const itemList = [
    { label: 'Home', onClick: () => { props.onClickHome(); handleItemClick('Home'); } },
    { label: 'About', onClick: () => { props.onClickAbout(); handleItemClick('About'); } },
    { label: 'Experience', onClick: () => { props.onClickExperience(); handleItemClick('Experience'); } },
    { label: 'Projects', onClick: () => { props.onClickProjects(); handleItemClick('Projects'); } },
    { label: 'Contact', onClick: () => { props.onClickContact(); handleItemClick('Contact'); } },
  ];

  const openPdf = () => {
    const pdfUrl = process.env.PUBLIC_URL + '/CV_RitaSaraiva.pdf';

    window.open(pdfUrl, '_blank');
  };

  return (
    <div className={classes.IDK}>
      <div className={classes.container}>
        <div className={classes.coisa}>
          <img src={Logo} alt="Logo" className={classes.ImagimeStyle} />
          <div className={classes.taskOptionsContainer}>
            <ul className={classes.buttonsContainer}>
              {itemList.map((item, index) => (
                <li
                  className={`${classes.buttonStyle} ${activeItem === item.label ? classes.activeButton : ''}`}
                  key={index}
                  onClick={item.onClick}
                >
                  <div className={classes.lineContainer}>
                    {item.label}
                    <Line lineWidth={activeItem === null ? 0 : 80} />
                  </div>
                </li>
              ))}
            </ul>
          </div>
          {/* Remove the Line component from here */}
          <button className={classes.resumeButtonContainer} onClick={openPdf}>
            RESUME
          </button>
        </div>
        {/* Move the Line component here */}
        <Line lineWidth={activeItem === null ? 0 : 80} />
      </div>
    </div>
  );
};

export default TaskBar;
